changeSet.sql:
CREATE TEMPORARY TABLE feedbacks_to_migrate SELECT f.reference AS text, POSITION(f.reference IN s.text) AS start_index, POSITION(f.reference IN s.text) + LENGTH(f.reference) AS end_index, r.submission_id, 'MANUAL' AS type, f.id AS feedback_id, SHA1(CONCAT(r.submission_id,';', POSITION(f.reference IN s.text),'-',POSITION(f.reference IN s.text) + LENGTH(f.reference),';',f.reference)) AS tb_id FROM exercise e INNER JOIN participation p ON e.id = p.exercise_id INNER JOIN submission s ON p.id = s.participation_id INNER JOIN result r ON p.id = r.participation_id and r.submission_id = s.id INNER JOIN feedback f ON r.id = f.result_id LEFT JOIN text_block b ON s.id = b.submission_id AND f.reference = b.id WHERE e.discriminator = 'T' AND f.reference IS NOT NULL AND f.reference NOT IN (SELECT id FROM text_block);
INSERT INTO feedbacks_to_migrate (text, start_index, end_index, submission_id, type, feedback_id, tb_id) SELECT f.reference, POSITION(f.reference IN s.text), POSITION(f.reference IN s.text) + LENGTH(f.reference), r.submission_id, 'MANUAL', f.id, SHA1(CONCAT(r.submission_id,';', POSITION(f.reference IN s.text),'-',POSITION(f.reference IN s.text) + LENGTH(f.reference),';',f.reference)) FROM feedback f INNER JOIN result r ON f.result_id = r.id INNER JOIN submission s ON r.submission_id = s.id WHERE r.participation_id IS NULL AND s.participation_id IS NULL AND length(f.reference) > 255;
INSERT INTO text_block (id, text, start_index, end_index, submission_id, type) SELECT ftm.tb_id, ftm.text, ftm.start_index, ftm.end_index, ftm.submission_id, ftm.type FROM feedbacks_to_migrate ftm;
UPDATE feedback f INNER JOIN feedbacks_to_migrate ftm ON f.id = ftm.feedback_id SET f.reference = ftm.tb_id WHERE f.id = ftm.feedback_id;
CREATE TEMPORARY TABLE leftover_feedbacks SELECT f.id AS feedback_id FROM feedback f INNER JOIN result r ON f.result_id = r.id WHERE r.participation_id IS NULL AND r.submission_id IS NULL;
DELETE FROM feedback WHERE id IN (SELECT feedback_id FROM leftover_feedbacks) OR result_id IS NULL;
