changeSet.sql:
UPDATE conversation c
SET name = REGEXP_REPLACE(name, '[-\\s]+', '-')
WHERE c.exercise_id IS NOT NULL OR c.lecture_id IS NOT NULL OR c.exam_id IS NOT NULL;

UPDATE conversation c
SET name = REGEXP_REPLACE(name, '[-\\s]+', '-', 'g')
WHERE c.exercise_id IS NOT NULL OR c.lecture_id IS NOT NULL OR c.exam_id IS NOT NULL;
